package loop;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//display 1 to 10
		
		int i = 1;
		int j = 1;
		while(i <= 10){
			System.out.println("times" + i);
			i++ ;
		
		}
		
		while( j<= 20){
			if (j% 2 == 0) {
				System.out.println(j);		
			}
			j++;
		}
		
		
		
		
		

	}

}
